import React, {Component} from 'react';
import classes from './Music.module.css';
import {addMusic, updateNewMusicText} from "../../redax/state";


const Music = (props) => {

    let newMusicElement = React.createRef()

    let addMusic = () => {
      let text = newMusicElement.current.value;
      props.addMusic();
    };

    let onNewsChange = () => {
        let text = newMusicElement.current.value;
        props.updateNewMusicText(text)
    };

    return <div>
        Music
        <div>
            <textarea ref={newMusicElement}/>
        </div>
        <div>
            <button onClick={addMusic}>Add Music</button>
        </div>
    </div>
}

export default Music;
